server/db.ts.content:
import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

// Configure websocket for serverless environment
if (typeof window === 'undefined') {
  neonConfig.webSocketConstructor = ws;
}

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ 
  connectionString: process.env.DATABASE_URL,
  max: 15, // Optimized connection pool size
  min: 3, // Minimum connections to maintain
  connectionTimeoutMillis: 8000, // Connection timeout
  idleTimeoutMillis: 60000, // Idle timeout (1 minute)
  acquireTimeoutMillis: 3000, // Timeout for acquiring connection
  allowExitOnIdle: false, // Keep connections alive
  application_name: 'liberty-bar-management'
});

export const db = drizzle({ client: pool, schema });

// Database connection monitoring
pool.on('connect', () => {
  console.log('Database connection established');
});

pool.on('error', (err) => {
  console.error('Database connection error:', err);
});

pool.on('remove', () => {
  console.log('Database connection removed from pool');
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import session from "express-session";
import cors from "cors";
import crypto from "crypto";
import cookieParser from "cookie-parser";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import { pool } from "./db";
import ConnectPgSimple from "connect-pg-simple";

const app = express();

const isProduction = process.env.NODE_ENV === 'production';

// HTTPS redirect middleware for production
if (isProduction) {
  app.use((req, res, next) => {
    if (req.header('x-forwarded-proto') !== 'https') {
      return res.redirect(`https://${req.header('host')}${req.url}`);
    }
    next();
  });
}

// CORS configuration for cross-origin requests
const corsOptions = {
  origin: function (origin: string | undefined, callback: (error: Error | null, allow?: boolean) => void) {
    // Allow requests with no origin (like mobile apps or curl requests)
    if (!origin) return callback(null, true);

    // Define allowed origins based on environment
    const allowedOrigins = isProduction 
      ? [
          process.env.FRONTEND_URL,
          process.env.REPLIT_DOMAIN ? `https://${process.env.REPLIT_DOMAIN}` : null,
          'https://liberty-bar-management.replit.app',
          // Add your custom domain here when deployed
        ].filter(Boolean) // Remove null values
      : [
          'http://localhost:5000',
          'http://localhost:3000',
          'http://127.0.0.1:5000',
          'http://127.0.0.1:3000',
          // Add the current Replit preview URL
          /https:\/\/.*\.replit\.dev$/,
          /https:\/\/.*\.replit\.app$/
        ];

    // Check if origin matches any allowed origin or pattern
    const isAllowed = allowedOrigins.some(allowedOrigin => {
      if (typeof allowedOrigin === 'string') {
        return origin === allowedOrigin;
      } else if (allowedOrigin instanceof RegExp) {
        return allowedOrigin.test(origin);
      }
      return false;
    });

    if (isAllowed) {
      callback(null, true);
    } else {
      // In development, be more permissive
      if (!isProduction) {
        console.log('CORS: Allowing origin in development:', origin);
        callback(null, true);
      } else {
        console.log('CORS: Blocking origin:', origin);
        callback(new Error('Not allowed by CORS'));
      }
    }
  },
  credentials: true, // Allow cookies to be sent
  optionsSuccessStatus: 200,
  methods: ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS'],
  allowedHeaders: ['Content-Type', 'Authorization', 'X-Requested-With']
};

app.use(cors(corsOptions));

// Check database connection before starting
async function checkDatabaseConnection() {
  try {
    const client = await pool.connect();
    await client.query('SELECT NOW()');
    client.release();
    log('Database connection verified');
    return true;
  } catch (error) {
    console.error('Database connection failed:', error);
    return false;
  }
}

// Log environment variables for production debugging
if (process.env.NODE_ENV === 'production') {
  log(`Environment variables check:`);
  log(`FRONTEND_URL: ${process.env.FRONTEND_URL || 'NOT SET'}`);
  log(`REPLIT_DOMAIN: ${process.env.REPLIT_DOMAIN || 'NOT SET'}`);
  log(`DATABASE_URL: ${process.env.DATABASE_URL ? 'SET' : 'NOT SET'}`);
  log(`SESSION_SECRET: ${process.env.SESSION_SECRET ? 'SET' : 'NOT SET'}`);
}

// Enhanced middleware configuration
app.use(express.json({ limit: '10mb' }));
app.use(express.urlencoded({ extended: false, limit: '10mb' }));
app.use(cookieParser()); // Add cookie parser middleware

// Security headers
app.use((req, res, next) => {
  res.set({
    'Cache-Control': 'no-store',
    'X-Content-Type-Options': 'nosniff',
    'X-Frame-Options': 'DENY',
    'X-XSS-Protection': '1; mode=block'
  });
  next();
});

// Configure session with simplified settings for debugging
const PgSession = ConnectPgSimple(session);

const sessionConfig = {
  store: new PgSession({
    pool: pool,
    tableName: 'sessions',
    createTableIfMissing: true,
    pruneSessionInterval: 60 * 15, // Clean up sessions every 15 minutes
    errorLog: (error: any) => {
      console.error('Session store error:', error);
    }
  }),
  secret: process.env.SESSION_SECRET || "liberty-bar-management-secret-key-2025",
  resave: false,
  saveUninitialized: false,
  rolling: true, // Reset expiration on activity
  name: 'liberty.session', // Use custom session name
  cookie: {
    secure: false, // Set to false for development
    httpOnly: false, // Set to false for debugging cookie issues
    maxAge: 8 * 60 * 60 * 1000, // 8 hours to match client session
    sameSite: 'lax', // Back to 'lax' for same-origin requests
    path: '/' // Ensure cookie is available for all paths
  }
};

app.use(session(sessionConfig));

// Session debugging middleware (can be removed in production)
if (process.env.NODE_ENV === 'development') {
  app.use((req, res, next) => {
    if (req.path.startsWith('/api') && req.path !== '/api/auth/user') {
      console.log(`[Session] ${req.method} ${req.path} - User: ${req.session?.user?.id || 'Anonymous'}`);
    }
    next();
  });
}

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

// Enhanced error handling middleware
app.use((err: any, req: Request, res: Response, next: NextFunction) => {
  console.error('Unhandled error:', err);

  if (err.code === 'ECONNREFUSED') {
    return res.status(503).json({ 
      message: 'Database connection failed',
      error: 'Service temporarily unavailable'
    });
  }

  if (err.name === 'ValidationError') {
    return res.status(400).json({ 
      message: 'Validation failed',
      error: err.message
    });
  }

  res.status(500).json({ 
    message: 'Internal server error',
    error: process.env.NODE_ENV === 'development' ? err.message : 'Something went wrong'
  });
});

// Graceful shutdown handling
const shutdown = async (signal: string) => {
  log(`${signal} received, shutting down gracefully`);

  try {
    await pool.end();
    log('Database pool closed');
    process.exit(0);
  } catch (error) {
    console.error('Error during shutdown:', error);
    process.exit(1);
  }
};

process.on('SIGTERM', () => shutdown('SIGTERM'));
process.on('SIGINT', () => shutdown('SIGINT'));

// Server startup
(async () => {
  try {
    // Verify database connection before starting server
    const dbConnected = await checkDatabaseConnection();
    if (!dbConnected) {
      log('Failed to connect to database, retrying in 5 seconds...');
      setTimeout(() => process.exit(1), 5000);
      return;
    }

    const server = await registerRoutes(app);

    // Setup development or production serving
    if (app.get("env") === "development") {
      await setupVite(app, server);
    } else {
      serveStatic(app);
    }

    // ALWAYS serve the app on port 5000
    const port = 5000;
    server.listen({
      port,
      host: "0.0.0.0",
      reusePort: true,
    }, () => {
      log(`üöÄ LIBERTY Bar Management System serving on port ${port}`);
      log(`üìä Database: Connected and ready`);
      log(`üîê Session store: PostgreSQL`);
      log(`üåê Environment: ${app.get("env")}`);
    });

  } catch (error) {
    log(`Failed to start server: ${error}`);
    process.exit(1);
  }
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { db } from "./db";
import { z } from "zod";
import {
  insertBarSessionSchema,
  insertTableSchema,
  insertProductSchema,
  insertCategorySchema,
  insertCreditClientSchema,
  insertOrderSchema,
  insertOrderItemSchema,
  insertPaymentSchema,
  insertAbsenceSchema,
  products, 
  orders, 
  orderItems, 
  categories, 
  tables, 
  payments, 
  barSessions, 
  users, 
  creditClients, 
  absences
} from "@shared/schema";
import { and, eq, gte, lt, sql, sum, count, desc, asc } from "drizzle-orm";

// Enhanced auth middleware for production
const requireAuth = (req: any, res: any, next: any) => {
  try {
    const session = req.session as any;
    console.log(`[DEBUG AUTH] Route: ${req.method} ${req.path}`);
    console.log(`[DEBUG AUTH] Session ID: ${session?.id || 'No session ID'}`);
    console.log(`[DEBUG AUTH] Session data:`, session ? Object.keys(session) : 'No session');
    console.log(`[DEBUG AUTH] Session user:`, session?.user);

    // Check if session exists and has user data
    if (!session || !session.user) {
      console.log(`[DEBUG AUTH] Auth failed - no session or user. Session: ${!!session}, User: ${!!session?.user}`);
      return res.status(401).json({ 
        message: "N√£o autorizado",
        details: "Sess√£o inv√°lida ou expirada" 
      });
    }

    // Validate user data integrity
    if (!session.user.id || !session.user.role) {
      console.log(`[DEBUG AUTH] Auth failed - invalid user data. ID: ${session.user.id}, Role: ${session.user.role}`);
      return res.status(401).json({ 
        message: "N√£o autorizado",
        details: "Dados de usu√°rio inv√°lidos" 
      });
    }

    // Update last activity for session tracking
    session.lastActivity = new Date().toISOString();

    // Attach user to request object
    req.user = session.user;
    console.log(`[DEBUG AUTH] Auth successful for user: ${req.user.id}, role: ${req.user.role}`);

    next();
  } catch (error) {
    console.error('Auth middleware error:', error);
    return res.status(500).json({ 
      message: "Erro de autentica√ß√£o",
      details: process.env.NODE_ENV === 'development' ? error.message : "Erro interno" 
    });
  }
};

const requireRole = (roles: string[]) => (req: any, res: any, next: any) => {
  if (!req.user || !roles.includes(req.user.role)) {
    return res.status(403).json({ message: "Acesso negado" });
  }
  next();
};

export async function registerRoutes(app: Express): Promise<Server> {
  // Cache user data for faster authentication
  const userCache = new Map();

  // Preload user data into cache at startup
  const preloadUserCache = async () => {
    try {
      const userIds = ['rafa', 'filinto', 'junior', 'server-001', 'jose.barros', 'milisiana', 'cashier-001', 'lucelle', 'carlmalack', 'manager', 'manager-001'];
      const users = await Promise.all(userIds.map(id => storage.getUser(id)));

      users.forEach(user => {
        if (user) {
          userCache.set(user.id, user);
        }
      });

      console.log(`Preloaded ${userCache.size} users into cache`);
    } catch (error) {
      console.error('Error preloading user cache:', error);
    }
  };

  // Initialize cache
  preloadUserCache();

  // Enhanced login route with improved security and error handling
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { username, password, role } = req.body;

      // Input validation
      if (!username || !password || !role) {
        return res.status(400).json({ 
          message: "Dados incompletos", 
          details: "Username, password e role s√£o obrigat√≥rios" 
        });
      }

      // Sanitize inputs
      const sanitizedUsername = username.trim().toLowerCase();
      const sanitizedRole = role.trim().toLowerCase();

      // Define user credentials with enhanced security
      const userCredentials = new Map([
        // Servers
        ['rafa', { password: 'Liberty@25%', role: 'server', active: true }],
        ['filinto', { password: 'Liberty@25%', role: 'server', active: true }],
        ['junior', { password: 'Liberty@25%', role: 'server', active: true }],
        ['server-001', { password: 'Liberty@25%', role: 'server', active: true }],
        // Cashiers
        ['jose.barros', { password: 'Liberty@25%', role: 'cashier', active: true }],
        ['milisiana', { password: 'Liberty@25%', role: 'cashier', active: true }],
        ['cashier-001', { password: 'Liberty@25%', role: 'cashier', active: true }],
        // Managers
        ['lucelle', { password: 'Bissau@25%', role: 'manager', active: true }],
        ['carlmalack', { password: 'Bissau@25%', role: 'manager', active: true }],
        ['manager', { password: 'Liberty@25%', role: 'manager', active: true }],
        ['manager-001', { password: 'Liberty@25%', role: 'manager', active: true }],
      ]);

      // Enhanced credential validation
      const userCreds = userCredentials.get(sanitizedUsername);

      if (!userCreds || userCreds.password !== password || userCreds.role !== sanitizedRole) {
        return res.status(401).json({ 
          message: "Credenciais inv√°lidas", 
          details: "Usu√°rio, senha ou role incorretos" 
        });
      }

      if (!userCreds.active) {
        return res.status(403).json({ 
          message: "Conta desativada", 
          details: "Contacte o administrador" 
        });
      }

      // Get user from cache first, fallback to database
      let user = userCache.get(sanitizedUsername);
      if (!user) {
        user = await storage.getUser(sanitizedUsername);
        if (user) {
          userCache.set(sanitizedUsername, user);
        }
      }

      if (!user) {
        return res.status(401).json({ 
          message: "Usu√°rio n√£o encontrado", 
          details: "Dados do usu√°rio n√£o dispon√≠veis" 
        });
      }

      // Check if user is active in database
      if (!user.isActive) {
        return res.status(403).json({ 
          message: "Conta desativada", 
          details: "Usu√°rio inativo no sistema" 
        });
      }

      // Store user in session with enhanced security
      if (!req.session) {
        return res.status(500).json({ 
          message: "Erro de sess√£o", 
          details: "Sess√£o n√£o dispon√≠vel" 
        });
      }

      (req.session as any).user = user;
      (req.session as any).loginTime = new Date().toISOString();
      (req.session as any).expiresAt = new Date(Date.now() + 8 * 60 * 60 * 1000).toISOString();
      (req.session as any).lastActivity = new Date().toISOString();
      (req.session as any).ipAddress = req.ip;
      (req.session as any).userAgent = req.get('User-Agent') || 'unknown';

      // Save session explicitly to ensure persistence BEFORE sending response
      await new Promise<void>((resolve, reject) => {
        req.session.save((err) => {
          if (err) {
            console.error('Session save error:', err);
            reject(new Error('Falha ao salvar sess√£o'));
          } else {
            console.log('Session saved successfully for user:', user.id);
            resolve();
          }
        });
      });

      // Log successful login
      console.log(`Login successful: ${user.firstName} ${user.lastName} (${user.role}) at ${new Date().toISOString()}`);

      // Return enhanced user data ONLY after session is saved
      return res.json({
        user: {
          id: user.id,
          email: user.email,
          firstName: user.firstName,
          lastName: user.lastName,
          role: user.role,
          isActive: user.isActive,
          profileImageUrl: user.profileImageUrl
        },
        session: {
          loginTime: (req.session as any).loginTime,
          expiresAt: (req.session as any).expiresAt || new Date(Date.now() + 8 * 60 * 60 * 1000).toISOString()
        },
        message: "Login realizado com sucesso"
      });

    } catch (error) {
      console.error('Login error:', error);
      res.status(500).json({ 
        message: "Erro interno do servidor", 
        details: process.env.NODE_ENV === 'development' ? error.message : "Tente novamente mais tarde" 
      });
    }
  });

  app.get("/api/auth/user", requireAuth, async (req: any, res) => {
    try {
      // Return comprehensive user data from session
      const user = req.user;

      // Validate user data before returning
      if (!user || !user.id || !user.role) {
        return res.status(401).json({ 
          message: "N√£o autorizado",
          details: "Dados de usu√°rio inv√°lidos" 
        });
      }

      // Return user data for authenticated users
      res.json({
        id: user.id,
        email: user.email,
        firstName: user.firstName,
        lastName: user.lastName,
        role: user.role,
        isActive: user.isActive,
        profileImageUrl: user.profileImageUrl,
        createdAt: user.createdAt,
        updatedAt: user.updatedAt
      });
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ 
        message: "Erro ao buscar usu√°rio",
        details: process.env.NODE_ENV === 'development' ? error.message : "Erro interno" 
      });
    }
  });

  // Helper endpoint to get dashboard redirect path based on user role
  app.get("/api/auth/redirect", requireAuth, async (req: any, res) => {
    try {
      const user = req.user;

      if (!user || !user.role) {
        return res.status(401).json({ 
          message: "N√£o autorizado",
          details: "Dados de usu√°rio inv√°lidos" 
        });
      }

      let dashboardPath = "/dashboard";
      let type = "operational";

      if (user.role === "manager") {
        dashboardPath = "/manager";
        type = "management";
      } else if (user.role === "cashier" || user.role === "server") {
        dashboardPath = "/dashboard";
        type = "operational";
      }

      res.json({
        dashboardPath,
        type,
        userRole: user.role
      });
    } catch (error) {
      console.error('Get redirect error:', error);
      res.status(500).json({ 
        message: "Erro interno do servidor",
        details: process.env.NODE_ENV === 'development' ? error.message : "Tente novamente mais tarde"
      });
    }
  });

  app.post("/api/auth/logout", (req, res) => {
    try {
      req.session.destroy((err) => {
        if (err) {
          console.error('Logout error:', err);
          return res.status(500).json({ message: "Erro ao fazer logout" });
        }
        res.clearCookie('liberty.session');
        res.json({ message: "Logout realizado com sucesso" });
      });
    } catch (error) {
      console.error('Logout error:', error);
      res.status(500).json({ message: "Erro ao fazer logout" });
    }
  });

  app.get("/api/auth/logout", (req, res) => {
    req.session.destroy((err) => {
      if (err) {
        return res.status(500).json({ message: "Erro ao fazer logout" });
