      if (userSession.user.role !== 'manager') {
        console.log(`[EXPORT] User role is ${userSession.user.role}, not manager`);
        return res.status(401).json({ message: "Not a manager" });
      }

      console.log(`[EXPORT] Auth passed for user: ${userSession.user.id}`);

      const sessionId = parseInt(req.params.sessionId);
      if (isNaN(sessionId)) {
        return res.status(400).json({ message: "ID da sess√£o inv√°lido" });
      }

      // Get session details
      const session = await storage.getBarSession(sessionId);
      if (!session) {
        console.log(`[DEBUG] Session ${sessionId} not found`);
        return res.status(404).json({ message: "Sess√£o n√£o encontrada" });
      }

      console.log(`[DEBUG] Found session: ${session.id}, user: ${session.userId}, shift: ${session.shiftType}`);

      // Use raw SQL to get data with proper field access
      const ordersWithProducts = await db.execute(sql`
        SELECT 
          o.id as order_id,
          o.created_at as order_date,
          p.name as product_name,
          p.price as product_price,
          oi.quantity,
          oi.unit_price,
          oi.total_price,
          COALESCE(c.name, 'Sem categoria') as category_name,
          t.number as table_number,
          COALESCE(u.first_name || ' ' || u.last_name, o.server_id) as server_name
        FROM orders o
        INNER JOIN order_items oi ON o.id = oi.order_id
        INNER JOIN products p ON oi.product_id = p.id
        LEFT JOIN categories c ON p.category_id = c.id
        LEFT JOIN tables t ON o.table_id = t.id
        LEFT JOIN users u ON o.server_id = u.id
        WHERE o.session_id = ${sessionId}
        ORDER BY o.created_at DESC, p.name
      `);

      console.log(`[DEBUG] Found ${ordersWithProducts.rows.length} order items for session ${sessionId}`);

      if (ordersWithProducts.rows.length === 0) {
        // Return empty data structure instead of error
        return res.json({
          sessionInfo: {
            id: session.id,
            date: session.createdAt?.toISOString().split('T')[0] || new Date().toISOString().split('T')[0],
