        const unitPrice = parseFloat(row.unit_price || "0");
        const quantity = parseInt(row.quantity || "0");

        console.log(`[DEBUG] Processing product: ${key}, unitPrice: ${unitPrice}, quantity: ${quantity}`);

        if (!acc[key]) {
          acc[key] = {
            productName: row.product_name,
            category: row.category_name || 'Sem categoria',
            totalQuantity: 0,
            totalRevenue: 0,
            unitPrice: unitPrice,
            orders: []
          };
        }
        acc[key].totalQuantity += quantity;
        acc[key].totalRevenue += (quantity * unitPrice);
        acc[key].orders.push({
          orderId: row.order_id,
          date: row.order_date,
          quantity: quantity,
          table: row.table_number,
          server: row.server_name
        });
        return acc;
      }, {} as Record<string, any>);

      // Format for CSV export
      const csvData = Object.values(productSummary).map((item: any) => ({
