  message: string;
  sessionInfo: {
    hasSession: boolean;
    sessionId: string | null;
    user: any;
    loginTime: string | null;
    cookie: {
      secure: boolean;
      httpOnly: boolean;
      maxAge: number | null;
      sameSite: string | null;
      domain: string | null;
    };
    timestamp: string;
  };
