import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { NewClientModal } from "@/components/modals/NewClientModal";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { CreditClient } from "@shared/schema";
import { Plus, Search, DollarSign, Calendar, User, CreditCard, Banknote, Smartphone, Minus } from "lucide-react";
import { useState } from "react";
import { PT } from "@/lib/i18n";
import { formatCurrency } from "@/lib/currency";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

export default function Credits() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedClient, setSelectedClient] = useState<CreditClient | null>(null);
  const [paymentAmount, setPaymentAmount] = useState("");
  const [paymentMethod, setPaymentMethod] = useState<"cash" | "mobile_money">("cash");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [showNewClientModal, setShowNewClientModal] = useState(false);
  const { toast } = useToast();

  const { data: creditClients, isLoading } = useQuery<CreditClient[]>({
    queryKey: ["/api/credit-clients"],
    refetchInterval: 10000,
  });

  // Ensure creditClients is always an array
  const clientsList = creditClients || [];

  const filteredClients = clientsList.filter(client =>
    client.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    client.email?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const totalCredit = clientsList.reduce((sum, client) => sum + parseFloat(client.totalCredit), 0);
  const activeClients = clientsList.filter(client => client.isActive && parseFloat(client.totalCredit) > 0);

  const formatDate = (date: string) => {
    return new Date(date).toLocaleDateString("pt-PT");
  };

  // Credit payment mutation
  const creditPaymentMutation = useMutation({
    mutationFn: async (paymentData: {
      clientId: number;
      amount: string;
      method: string;
      phoneNumber?: string;
    }) => {
      return apiRequest("POST", "/api/credit-payments", paymentData);
    },
    onSuccess: () => {
      toast({
        title: "Sucesso",
        description: "Pagamento de cr√©dito processado com sucesso",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/credit-clients"] });
      handleClosePaymentModal();
    },
    onError: (error) => {
      toast({
        title: "Erro",
        description: "Erro ao processar pagamento",
        variant: "destructive",
      });
    },
  });

  const handleOpenPaymentModal = (client: CreditClient) => {
    setSelectedClient(client);
    setShowPaymentModal(true);
  };

  const handleClosePaymentModal = () => {
    setSelectedClient(null);
    setPaymentAmount("");
    setPaymentMethod("cash");
    setPhoneNumber("");
    setShowPaymentModal(false);
  };

  const handleClientCreated = (client: CreditClient) => {
    queryClient.invalidateQueries({ queryKey: ["/api/credit-clients"] });
    setShowNewClientModal(false);
    toast({
      title: "Sucesso",
      description: `Cliente ${client.name} criado com sucesso`,
    });
  };

  const handlePayment = () => {
    if (!selectedClient || !paymentAmount) {
      toast({
        title: "Erro",
        description: "Por favor, preencha todos os campos",
        variant: "destructive",
      });
      return;
    }

    const amount = parseFloat(paymentAmount);
    const clientCredit = parseFloat(selectedClient.totalCredit);

    if (amount <= 0) {
      toast({
        title: "Erro",
        description: "Valor deve ser maior que zero",
        variant: "destructive",
      });
      return;
    }

    if (amount > clientCredit) {
      toast({
        title: "Erro",
        description: "Valor n√£o pode ser maior que o cr√©dito dispon√≠vel",
        variant: "destructive",
      });
      return;
    }

    if (paymentMethod === "mobile_money" && !phoneNumber) {
      toast({
        title: "Erro",
        description: "Por favor, insira o n√∫mero de telefone",
        variant: "destructive",
      });
      return;
    }

    const paymentData = {
      clientId: selectedClient.id,
      amount: amount.toString(),
      method: paymentMethod,
      phoneNumber: paymentMethod === "mobile_money" ? phoneNumber : undefined,
    };

    creditPaymentMutation.mutate(paymentData);
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <h2 className="text-2xl font-bold text-white mb-6">{PT.credits.title}</h2>
        <div className="space-y-4">
          {[1, 2, 3, 4].map((i) => (
            <Card key={i} className="bg-gray-800 border-gray-700">
              <CardContent className="p-4">
                <div className="animate-pulse">
                  <div className="h-4 bg-gray-600 rounded w-3/4 mb-2"></div>
                  <div className="h-3 bg-gray-600 rounded w-1/2"></div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-2xl font-bold text-white">{PT.credits.title}</h2>
        <Button 
          onClick={() => setShowNewClientModal(true)}
          className="bg-orange-500 hover:bg-orange-600 text-white"
        >
          <Plus className="w-4 h-4 mr-2" />
          Novo Cliente
        </Button>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">Total Cr√©ditos</p>
                <p className="text-2xl font-bold text-orange-400">{formatCurrency(totalCredit)}</p>
              </div>
              <div className="w-12 h-12 bg-orange-500 bg-opacity-20 rounded-full flex items-center justify-center">
                <DollarSign className="w-6 h-6 text-orange-400" />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">Clientes Ativos</p>
                <p className="text-2xl font-bold text-green-400">{activeClients.length}</p>
              </div>
              <div className="w-12 h-12 bg-green-500 bg-opacity-20 rounded-full flex items-center justify-center">
                <User className="w-6 h-6 text-green-400" />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">Total Clientes</p>
                <p className="text-2xl font-bold text-white">{clientsList.length}</p>
              </div>
              <div className="w-12 h-12 bg-blue-500 bg-opacity-20 rounded-full flex items-center justify-center">
                <User className="w-6 h-6 text-blue-400" />
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Search */}
      <div className="mb-6">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
          <Input
            placeholder="Pesquisar cliente..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10 bg-gray-800 border-gray-700 text-white"
          />
        </div>
      </div>

      {/* Clients List */}
      <div className="space-y-4">
        {filteredClients.length === 0 ? (
          <Card className="bg-gray-800 border-gray-700">
            <CardContent className="p-8 text-center">
              <p className="text-gray-400">
                {searchTerm ? "Aucun client trouv√©" : "Aucun client √† cr√©dit"}
              </p>
            </CardContent>
          </Card>
        ) : (
          filteredClients.map((client) => (
            <Card key={client.id} className="bg-gray-800 border-gray-700">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div className="flex-1">
                    <div className="flex items-center space-x-4">
                      <div className="w-12 h-12 bg-blue-600 rounded-full flex items-center justify-center">
                        <User className="w-6 h-6 text-white" />
                      </div>
                      <div>
                        <h3 className="text-lg font-semibold text-white">{client.name}</h3>
                        <div className="flex items-center space-x-4 text-sm text-gray-400">
                          {client.email && (
                            <span>{client.email}</span>
                          )}
                          {client.phone && (
                            <span>{client.phone}</span>
                          )}
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center space-x-6">
                    <div className="text-right">
                      <p className="text-sm text-gray-400">Cr√©dit actuel</p>
                      <p className="text-xl font-bold text-orange-400">{formatCurrency(client.totalCredit)}</p>
                    </div>

                    <div className="text-right">
                      <p className="text-sm text-gray-400">Limite</p>
                      <p className="text-sm text-white">{formatCurrency(client.creditLimit)}</p>
                    </div>

                    <div className="text-right">
                      <p className="text-sm text-gray-400">Derni√®re mise √† jour</p>
                      <p className="text-sm text-white">{formatDate(client.updatedAt!)}</p>
                    </div>

                    <div className="flex items-center space-x-2">
                      <Badge
                        variant={client.isActive ? "default" : "secondary"}
                        className={client.isActive ? "bg-green-500" : "bg-gray-500"}
                      >
                        {client.isActive ? "Actif" : "Inactif"}
                      </Badge>
                    </div>

                    <div className="flex space-x-2">
                      <Button
                        size="sm"
                        className="bg-green-600 hover:bg-green-700 text-white"
                        onClick={() => handleOpenPaymentModal(client)}
                        disabled={parseFloat(client.totalCredit) <= 0}
                      >
                        <Minus className="w-4 h-4 mr-1" />
                        Paiement
                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        className="border-gray-600 text-gray-300 hover:bg-gray-700"
                      >
                        Historique
                      </Button>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>

      {/* Payment Modal */}
      <Dialog open={showPaymentModal} onOpenChange={setShowPaymentModal}>
        <DialogContent className="bg-gray-800 border-gray-700 text-white max-w-md">
          <DialogHeader>
            <DialogTitle className="text-lg font-semibold">
              Paiement de Cr√©dit - {selectedClient?.name}
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            {/* Client Credit Info */}
            <Card className="bg-gray-700 border-gray-600">
              <CardContent className="p-4">
                <div className="flex justify-between items-center">
                  <span className="text-sm text-gray-400">Cr√©dit actuel</span>
                  <span className="text-xl font-bold text-orange-400">
                    {selectedClient && formatCurrency(selectedClient.totalCredit)}
                  </span>
                </div>
              </CardContent>
            </Card>

            {/* Payment Amount */}
            <div className="space-y-2">
              <Label htmlFor="amount" className="text-sm font-medium">
                Montant du paiement
              </Label>
              <Input
                id="amount"
                type="number"
                step="0.01"
                min="0"
                max={selectedClient?.totalCredit}
                value={paymentAmount}
                onChange={(e) => setPaymentAmount(e.target.value)}
                placeholder="0.00"
                className="bg-gray-700 border-gray-600 text-white"
              />
              <div className="flex justify-between text-sm text-gray-400">
                <span>Max: {selectedClient && formatCurrency(selectedClient.totalCredit)}</span>
                <Button
                  type="button"
                  variant="link"
                  className="p-0 h-auto text-blue-400 hover:text-blue-300"
                  onClick={() => setPaymentAmount(selectedClient?.totalCredit || "0")}
                >
                  Montant total
                </Button>
              </div>
            </div>

            {/* Payment Method Selection */}
            <div className="space-y-2">
              <Label className="text-sm font-medium">M√©thode de paiement</Label>
              <div className="grid grid-cols-2 gap-3">
                <Button
                  onClick={() => setPaymentMethod("cash")}
                  variant={paymentMethod === "cash" ? "default" : "outline"}
                  className={`py-6 flex-col ${
                    paymentMethod === "cash"
                      ? "bg-green-600 hover:bg-green-700"
                      : "border-gray-600 hover:bg-gray-700"
                  }`}
                >
                  <Banknote className="w-5 h-5 mb-1" />
                  <span className="text-sm">Esp√®ces</span>
                </Button>
                <Button
                  onClick={() => setPaymentMethod("mobile_money")}
                  variant={paymentMethod === "mobile_money" ? "default" : "outline"}
                  className={`py-6 flex-col ${
                    paymentMethod === "mobile_money"
                      ? "bg-blue-600 hover:bg-blue-700"
                      : "border-gray-600 hover:bg-gray-700"
                  }`}
                >
                  <Smartphone className="w-5 h-5 mb-1" />
                  <span className="text-sm">Mobile Money</span>
                </Button>
              </div>
            </div>

            {/* Mobile Money Phone Number */}
            {paymentMethod === "mobile_money" && (
              <div className="space-y-2">
                <Label htmlFor="phone" className="text-sm font-medium">
                  Num√©ro de t√©l√©phone
                </Label>
                <Input
                  id="phone"
                  type="tel"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  placeholder="+XXX XXX XXX XXX"
                  className="bg-gray-700 border-gray-600 text-white"
                />
              </div>
            )}

            {/* New Credit Balance Preview */}
            {paymentAmount && (
              <Card className="bg-gray-700 border-gray-600">
                <CardContent className="p-4">
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-400">Nouveau solde</span>
                    <span className="text-lg font-bold text-green-400">
                      {selectedClient && formatCurrency(
                        (parseFloat(selectedClient.totalCredit) - parseFloat(paymentAmount || "0")).toFixed(2)
                      )}
                    </span>
                  </div>
                </CardContent>
              </Card>
            )}

            {/* Action Buttons */}
            <div className="flex space-x-3">
              <Button
                onClick={handleClosePaymentModal}
                variant="outline"
                className="flex-1 border-gray-600 hover:bg-gray-700"
              >
                Annuler
              </Button>
              <Button
                onClick={handlePayment}
                disabled={creditPaymentMutation.isPending || !paymentAmount || parseFloat(paymentAmount) <= 0}
                className="flex-1 bg-green-600 hover:bg-green-700"
              >
                {creditPaymentMutation.isPending ? "Traitement..." : "Confirmer Paiement"}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* New Client Modal */}
      <NewClientModal
        open={showNewClientModal}
        onOpenChange={setShowNewClientModal}
        onClientCreated={handleClientCreated}
      />
    </div>
  );
}

client/src/pages/Dashboard.tsx.content:
import { useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useBarStore } from "@/store/useBarStore";
import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";
import { StatsCards } from "@/components/dashboard/StatsCards";
import { OrdersPanel } from "@/components/dashboard/OrdersPanel";
import { TablesOverview } from "@/components/dashboard/TablesOverview";
import { CreditClientsTable } from "@/components/dashboard/CreditClientsTable";
import { SessionControls } from "@/components/dashboard/SessionControls";
import { StockPanel } from "@/components/dashboard/StockPanel";
import { RefreshCw } from "lucide-react";
import { SessionStats } from "@shared/schema";
import { PT } from "@/lib/i18n";

export default function Dashboard() {
  const { user } = useAuth();
  const { activeSession, setSessionStats } = useBarStore();

  const { data: sessionStats, refetch } = useQuery<SessionStats>({
    queryKey: ["/api/sessions", activeSession?.id, "stats"],
    enabled: !!activeSession,
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  useEffect(() => {
    if (sessionStats) {
      setSessionStats(sessionStats);
    }
  }, [sessionStats, setSessionStats]);

  const handleRefresh = () => {
    refetch();
  };

  return (
    <div className="p-6">
      {/* Dashboard Header */}
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-2xl font-bold text-white">
          {PT.dashboard.title} - {user?.role === "cashier" ? PT.roles.cashier : user?.role === "server" ? PT.roles.server : PT.roles.manager}
        </h2>
        <div className="flex items-center space-x-4">
          <div className="text-sm text-gray-400">
            √öltima atualiza√ß√£o: {new Date().toLocaleTimeString("pt-PT", { hour: "2-digit", minute: "2-digit" })}
          </div>
          <Button
            onClick={handleRefresh}
            className="bg-blue-600 hover:bg-blue-700 text-white"
          >
            <RefreshCw className="w-4 h-4 mr-2" />
            Atualizar
          </Button>
        </div>
      </div>

      {/* Session Controls */}
      <SessionControls />

      {/* Stats Cards */}
      <StatsCards />

      {/* Three Column Layout for Cashiers */}
      {user?.role === "cashier" ? (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Orders Panel */}
          <OrdersPanel />

          {/* Tables Overview */}
          <TablesOverview />

          {/* Stock Panel for Cashiers */}
          <StockPanel />
        </div>
      ) : (
        /* Two Column Layout for Servers */
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Orders Panel */}
          <OrdersPanel />

          {/* Tables Overview */}
          <TablesOverview />
        </div>
      )}

      {/* Credits Section */}
      <div className="mt-6">
        <CreditClientsTable />
      </div>
    </div>
  );
}

client/src/pages/Inventory.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { 
  Plus, 
  Edit, 
  Trash2, 
  Package, 
  AlertTriangle,
  Image as ImageIcon,
  Search,
  Filter
} from "lucide-react";
import { useState } from "react";
import { formatCurrency } from "@/lib/currency";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Product, Category } from "@shared/schema";

export default function Inventory() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState<string>("all");
  const [showAddModal, setShowAddModal] = useState(false);
  const [showCategoryModal, setShowCategoryModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [editingCategory, setEditingCategory] = useState<Category | null>(null);
  const [newProduct, setNewProduct] = useState({
    name: "",
    description: "",
    categoryId: "",
    price: "",
    stock: "",
    minStock: "",
    maxStock: "",
  });
  const [newCategory, setNewCategory] = useState({
    name: "",
    description: "",
  });
  const { toast } = useToast();

  const { data: products = [], isLoading: productsLoading } = useQuery<Product[]>({
    queryKey: ["/api/products"],
    refetchInterval: 5000,
    staleTime: 0, // Data is always stale, always refetch
    cacheTime: 0, // Don't cache data
  });

  const { data: categories = [] } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
    staleTime: 0,
  });

  const createProductMutation = useMutation({
    mutationFn: async (productData: any) => {
      return apiRequest("POST", "/api/products", productData);
    },
    onSuccess: () => {
      toast({
        title: "Succ√®s",
        description: "Produit cr√©√© avec succ√®s",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      queryClient.refetchQueries({ queryKey: ["/api/products"] });
      handleCloseModal();
    },
    onError: () => {
      toast({
        title: "Erreur",
        description: "Erreur lors de la cr√©ation du produit",
        variant: "destructive",
      });
    },
  });

  const updateProductMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: any }) => {
      return apiRequest("PUT", `/api/products/${id}`, data);
    },
    onSuccess: () => {
      toast({
        title: "Succ√®s",
        description: "Produit mis √† jour avec succ√®s",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      queryClient.refetchQueries({ queryKey: ["/api/products"] });
      handleCloseModal();
    },
    onError: () => {
      toast({
        title: "Erreur",
        description: "Erreur lors de la mise √† jour du produit",
        variant: "destructive",
      });
    },
  });

  const deleteProductMutation = useMutation({
    mutationFn: async (id: number) => {
      return apiRequest("DELETE", `/api/products/${id}`);
    },
    onSuccess: () => {
      toast({
        title: "Succ√®s",
        description: "Produit supprim√© avec succ√®s",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      queryClient.refetchQueries({ queryKey: ["/api/products"] });
    },
    onError: () => {
      toast({
        title: "Erreur",
        description: "Erreur lors de la suppression du produit",
        variant: "destructive",
      });
    },
  });

  const createCategoryMutation = useMutation({
    mutationFn: async (categoryData: any) => {
      return apiRequest("POST", "/api/categories", categoryData);
    },
    onSuccess: () => {
      toast({
        title: "Succ√®s",
        description: "Cat√©gorie cr√©√©e avec succ√®s",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/categories"] });
      queryClient.refetchQueries({ queryKey: ["/api/categories"] });
      handleCloseCategoryModal();
    },
    onError: () => {
      toast({
        title: "Erreur",
        description: "Erreur lors de la cr√©ation de la cat√©gorie",
        variant: "destructive",
      });
    },
  });

  const updateCategoryMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: any }) => {
      return apiRequest("PUT", `/api/categories/${id}`, data);
    },
    onSuccess: () => {
      toast({
        title: "Succ√®s",
        description: "Cat√©gorie mise √† jour avec succ√®s",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/categories"] });
      queryClient.refetchQueries({ queryKey: ["/api/categories"] });
      handleCloseCategoryModal();
    },
    onError: () => {
      toast({
        title: "Erreur",
        description: "Erreur lors de la mise √† jour de la cat√©gorie",
        variant: "destructive",
      });
    },
  });

  const filteredProducts = products.filter(product => {
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         product.description?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === "all" || product.categoryId.toString() === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const lowStockProducts = products.filter(product => 
    product.stock !== null && product.minStock !== null && product.stock <= product.minStock
  );

  const handleCloseModal = () => {
    setShowAddModal(false);
    setEditingProduct(null);
    setNewProduct({
      name: "",
      description: "",
      categoryId: "",
      price: "",
      stock: "",
      minStock: "",
      maxStock: "",
    });
  };

  const handleCloseCategoryModal = () => {
    setShowCategoryModal(false);
    setEditingCategory(null);
    setNewCategory({
      name: "",
      description: "",
    });
  };

  const handleSubmit = () => {
    if (!newProduct.name || !newProduct.categoryId || !newProduct.price) {
      toast({
        title: "Erreur",
        description: "Veuillez remplir tous les champs obligatoires",
        variant: "destructive",
      });
      return;
    }

    const productData = {
      name: newProduct.name,
      description: newProduct.description || "",
      categoryId: parseInt(newProduct.categoryId),
      price: newProduct.price, // Garder comme cha√Æne pour le sch√©ma decimal
      stockQuantity: parseInt(newProduct.stock || "0"),
      minStockLevel: parseInt(newProduct.minStock || "0"),
      imageUrl: null, // Pour l'instant, pas de support d'image
      isActive: true
    };

    if (editingProduct) {
      updateProductMutation.mutate({ id: editingProduct.id, data: productData });
    } else {
      createProductMutation.mutate(productData);
    }
  };

  const handleEdit = (product: Product) => {
    setEditingProduct(product);
    setNewProduct({
      name: product.name,
      description: product.description || "",
      categoryId: product.categoryId.toString(),
      price: product.price,
      stock: product.stock?.toString() || "",
      minStock: product.minStock?.toString() || "",
      maxStock: product.maxStock?.toString() || "",
    });
    setShowAddModal(true);
  };

  const handleDelete = (id: number) => {
    if (confirm("√ätes-vous s√ªr de vouloir supprimer ce produit ?")) {
      deleteProductMutation.mutate(id);
    }
  };

  const handleSubmitCategory = () => {
    if (!newCategory.name) {
      toast({
        title: "Erreur",
        description: "Veuillez saisir un nom pour la cat√©gorie",
        variant: "destructive",
      });
      return;
    }

    const categoryData = {
      name: newCategory.name,
      description: newCategory.description || "",
    };

    if (editingCategory) {
      updateCategoryMutation.mutate({ id: editingCategory.id, data: categoryData });
    } else {
      createCategoryMutation.mutate(categoryData);
    }
  };

  const handleEditCategory = (category: Category) => {
    setEditingCategory(category);
    setNewCategory({
      name: category.name,
      description: category.description || "",
    });
    setShowCategoryModal(true);
  };

  const getStockStatus = (product: Product) => {
    if (product.stock === null) return { status: "unknown", color: "bg-gray-500" };
    if (product.minStock !== null && product.stock <= product.minStock) return { status: "low", color: "bg-red-500" };
    if (product.maxStock !== null && product.stock >= product.maxStock * 0.8) return { status: "high", color: "bg-green-500" };
    return { status: "medium", color: "bg-orange-500" };
  };

  if (productsLoading) {
    return (
      <div className="p-6">
        <div className="animate-pulse space-y-4">
          {[1, 2, 3, 4].map((i) => (
            <div key={i} className="h-32 bg-gray-700 rounded-lg"></div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-white">Gestion du Stock</h1>
          <p className="text-gray-400 mt-1">G√©rez vos produits et surveillez votre inventaire</p>
        </div>
        <Dialog open={showAddModal} onOpenChange={setShowAddModal}>
          <DialogTrigger asChild>
            <Button className="bg-green-600 hover:bg-green-700">
              <Plus className="w-4 h-4 mr-2" />
              Nouveau Produit
            </Button>
          </DialogTrigger>
          <DialogContent className="bg-gray-800 border-gray-700 text-white max-w-2xl">
            <DialogHeader>
              <DialogTitle>
                {editingProduct ? "Modifier le produit" : "Nouveau produit"}
              </DialogTitle>
            </DialogHeader>
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="name">Nom du produit *</Label>
                  <Input
                    id="name"
                    value={newProduct.name}
                    onChange={(e) => setNewProduct({...newProduct, name: e.target.value})}
                    className="bg-gray-700 border-gray-600"
                  />
                </div>
                <div>
                  <Label htmlFor="category">Cat√©gorie *</Label>
                  <Select value={newProduct.categoryId} onValueChange={(value) => setNewProduct({...newProduct, categoryId: value})}>
                    <SelectTrigger className="bg-gray-700 border-gray-600">
                      <SelectValue placeholder="S√©lectionner une cat√©gorie" />
                    </SelectTrigger>
                    <SelectContent className="bg-gray-700 border-gray-600">
                      {categories.map((category) => (
                        <SelectItem key={category.id} value={category.id.toString()}>
                          {category.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div>
                <Label htmlFor="description">Description</Label>
                <Textarea
                  id="description"
                  value={newProduct.description}
                  onChange={(e) => setNewProduct({...newProduct, description: e.target.value})}
                  className="bg-gray-700 border-gray-600"
                  rows={3}
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="price">Prix *</Label>
                  <Input
                    id="price"
                    type="number"
                    step="0.01"
                    value={newProduct.price}
                    onChange={(e) => setNewProduct({...newProduct, price: e.target.value})}
                    className="bg-gray-700 border-gray-600"
                  />
                </div>
                <div>
                  <Label htmlFor="stock">Stock actuel</Label>
                  <Input
                    id="stock"
                    type="number"
                    value={newProduct.stock}
                    onChange={(e) => setNewProduct({...newProduct, stock: e.target.value})}
                    className="bg-gray-700 border-gray-600"
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="minStock">Stock minimum</Label>
                  <Input
                    id="minStock"
                    type="number"
                    value={newProduct.minStock}
                    onChange={(e) => setNewProduct({...newProduct, minStock: e.target.value})}
                    className="bg-gray-700 border-gray-600"
                  />
                </div>
                <div>
                  <Label htmlFor="maxStock">Stock maximum</Label>
                  <Input
                    id="maxStock"
                    type="number"
                    value={newProduct.maxStock}
                    onChange={(e) => setNewProduct({...newProduct, maxStock: e.target.value})}
                    className="bg-gray-700 border-gray-600"
                  />
                </div>
              </div>



              <div className="flex space-x-3">
                <Button
                  onClick={handleCloseModal}
                  variant="outline"
                  className="flex-1 border-gray-600 hover:bg-gray-700"
                >
                  Annuler
                </Button>
                <Button
                  onClick={handleSubmit}
                  disabled={createProductMutation.isPending || updateProductMutation.isPending}
                  className="flex-1 bg-green-600 hover:bg-green-700"
                >
                  {editingProduct ? "Modifier" : "Cr√©er"}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">Total Produits</p>
                <p className="text-2xl font-bold text-white">{products.length}</p>
              </div>
              <Package className="w-8 h-8 text-blue-400" />
            </div>
          </CardContent>
        </Card>

        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">Stock Faible</p>
                <p className="text-2xl font-bold text-red-400">{lowStockProducts.length}</p>
              </div>
              <AlertTriangle className="w-8 h-8 text-red-400" />
            </div>
          </CardContent>
        </Card>

        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">Cat√©gories</p>
                <p className="text-2xl font-bold text-green-400">{categories.length}</p>
              </div>
              <Filter className="w-8 h-8 text-green-400" />
            </div>
          </CardContent>
        </Card>

        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">Valeur Stock</p>
                <p className="text-2xl font-bold text-purple-400">
                  {formatCurrency(
                    products.reduce((sum, p) => sum + (parseFloat(p.price) * (p.stock || 0)), 0).toFixed(2)
                  )}
                </p>
              </div>
              <Package className="w-8 h-8 text-purple-400" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <div className="flex items-center space-x-4">
        <div className="flex-1 relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
          <Input
            placeholder="Rechercher un produit..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10 bg-gray-800 border-gray-700 text-white"
          />
        </div>
        <Select value={selectedCategory} onValueChange={setSelectedCategory}>
          <SelectTrigger className="w-48 bg-gray-800 border-gray-700 text-white">
            <SelectValue />
          </SelectTrigger>
          <SelectContent className="bg-gray-800 border-gray-700">
            <SelectItem value="all">Toutes les cat√©gories</SelectItem>
            {categories.map((category) => (
              <SelectItem key={category.id} value={category.id.toString()}>
                {category.name}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
        <Button
          onClick={() => setShowCategoryModal(true)}
          variant="outline"
          className="border-gray-600 hover:bg-gray-700 text-white"
        >
          <Plus className="w-4 h-4 mr-2" />
          Cat√©gorie
        </Button>
      </div>

      {/* Categories Management */}
      <div className="space-y-4">
        <h3 className="text-lg font-semibold text-white">Gestion des Cat√©gories</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
          {categories.map((category) => (
            <Card key={category.id} className="bg-gray-800 border-gray-700">
              <CardContent className="p-4">
                <div className="flex items-center justify-between">
                  <div>
                    <h4 className="font-medium text-white">{category.name}</h4>
                    {category.description && (
                      <p className="text-sm text-gray-400 mt-1">{category.description}</p>
                    )}
                  </div>
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={() => handleEditCategory(category)}
                    className="text-blue-400 hover:text-blue-300"
                  >
                    <Edit className="w-4 h-4" />
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>

      {/* Products Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
        {filteredProducts.map((product) => {
          const stockStatus = getStockStatus(product);
          const category = categories.find(c => c.id === product.categoryId);

          return (
            <Card key={product.id} className="bg-gray-800 border-gray-700">
              <CardContent className="p-4">
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center space-x-2">
                    <div className={`w-3 h-3 rounded-full ${stockStatus.color}`}></div>
                    <Badge variant="secondary" className="text-xs">
                      {category?.name}
                    </Badge>
                  </div>
                  <div className="flex items-center space-x-1">
                    <Button
                      size="sm"
                      variant="ghost"
                      onClick={() => handleEdit(product)}
                      className="text-blue-400 hover:text-blue-300"
                    >
                      <Edit className="w-4 h-4" />
                    </Button>
                    <Button
                      size="sm"
                      variant="ghost"
                      onClick={() => handleDelete(product.id)}
                      className="text-red-400 hover:text-red-300"
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                </div>

                <div className="space-y-2">
                  <h3 className="font-semibold text-white">{product.name}</h3>
                  {product.description && (
                    <p className="text-sm text-gray-400 line-clamp-2">{product.description}</p>
                  )}
                  <div className="flex items-center justify-between">
                    <span className="text-lg font-bold text-green-400">
                      {formatCurrency(product.price)}
                    </span>
                    <span className="text-sm text-gray-400">
                      Stock: {product.stock || 0}
                    </span>
                  </div>
                  {product.minStock !== null && product.stock !== null && product.stock <= product.minStock && (
                    <div className="flex items-center space-x-1 text-red-400">
                      <AlertTriangle className="w-4 h-4" />
                      <span className="text-xs">Stock faible</span>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {filteredProducts.length === 0 && (
        <div className="text-center py-12">
          <Package className="w-12 h-12 text-gray-400 mx-auto mb-4" />
          <p className="text-gray-400">Aucun produit trouv√©</p>
        </div>
      )}

      {/* Category Modal */}
      <Dialog open={showCategoryModal} onOpenChange={setShowCategoryModal}>
        <DialogContent className="bg-gray-800 border-gray-700 text-white">
          <DialogHeader>
            <DialogTitle>
              {editingCategory ? "Modifier la cat√©gorie" : "Nouvelle cat√©gorie"}
            </DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <Label htmlFor="categoryName">Nom de la cat√©gorie *</Label>
              <Input
                id="categoryName"
                value={newCategory.name}
                onChange={(e) => setNewCategory({...newCategory, name: e.target.value})}
                className="bg-gray-700 border-gray-600"
                placeholder="Ex: Boissons, Entr√©es, Plats..."
              />
            </div>
            <div>
              <Label htmlFor="categoryDescription">Description</Label>
              <Textarea
                id="categoryDescription"
                value={newCategory.description}
                onChange={(e) => setNewCategory({...newCategory, description: e.target.value})}
                className="bg-gray-700 border-gray-600"
                placeholder="Description de la cat√©gorie (optionnel)"
                rows={3}
              />
            </div>
            <div className="flex space-x-3">
              <Button
                onClick={handleCloseCategoryModal}
                variant="outline"
                className="flex-1 border-gray-600 hover:bg-gray-700"
              >
                Annuler
              </Button>
              <Button
                onClick={handleSubmitCategory}
                disabled={createCategoryMutation.isPending || updateCategoryMutation.isPending}
                className="flex-1 bg-green-600 hover:bg-green-700"
              >
                {editingCategory ? "Modifier" : "Cr√©er"}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

