import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { 
  Dialog, 
  DialogContent, 
  DialogHeader, 
  DialogTitle, 
  DialogTrigger 
} from "@/components/ui/dialog";
import { 
  History, 
  Receipt, 
  Search, 
  Eye, 
  Calendar,
  DollarSign,
  Clock,
  User,
  Printer
} from "lucide-react";
import { formatCurrency } from "@/lib/currency";
import { thermalPrinter } from "@/lib/thermalPrinter";
import { useToast } from "@/hooks/use-toast";
import type { OrderWithItems } from "@shared/schema";

export default function SalesHistory() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);

  const { data: completedOrders = [], isLoading } = useQuery<OrderWithItems[]>({
    queryKey: ["/api/orders", "completed", selectedDate],
    queryFn: async () => {
      const response = await fetch(`/api/orders?status=completed&date=${selectedDate}`, {
        credentials: 'include'
      });
      if (!response.ok) throw new Error('Failed to fetch orders');
      return response.json();
    },
    refetchInterval: 30000,
  });

  const filteredOrders = completedOrders.filter(order => {
    const searchLower = searchTerm.toLowerCase();
    return (
      order.table?.number.toString().includes(searchLower) ||
      order.server?.firstName?.toLowerCase().includes(searchLower) ||
      order.clientName?.toLowerCase().includes(searchLower) ||
      order.items.some(item => item.product.name.toLowerCase().includes(searchLower))
    );
  });

  const getPaymentMethodBadge = (method: string) => {
    const colors = {
      cash: "bg-green-500",
      mobile_money: "bg-blue-500",
      credit: "bg-orange-500",
      partial: "bg-purple-500"
    };
    const labels = {
      cash: "Dinheiro",
      mobile_money: "Mobile Money",
      credit: "Cr√©dito",
      partial: "Parcial"
    };
    return (
      <Badge className={`${colors[method as keyof typeof colors]} text-white`}>
        {labels[method as keyof typeof labels]}
      </Badge>
    );
  };

  const handlePrintReceipt = async (order: OrderWithItems) => {
    try {
      // Convert order data to receipt format
      const receiptData = {
        orderNumber: order.id.toString(),
        tableName: `Mesa ${order.table?.number || order.tableId}`,
        clientName: order.clientName || order.creditClient?.name,
        items: order.items.map((item: any) => ({
          name: item.product?.name || 'Produto',
          quantity: item.quantity,
          price: `${parseFloat(item.unitPrice || 0).toFixed(0)}`,
          total: `${parseFloat(item.totalPrice || 0).toFixed(0)}`
        })),
        subtotal: `${parseFloat(order.totalAmount).toFixed(0)}`,
        total: `${parseFloat(order.totalAmount).toFixed(0)}`,
        paymentMethod: order.payments?.[0]?.method === "cash" ? "Dinheiro" : 
                      order.payments?.[0]?.method === "mobile_money" ? "Mobile Money" : 
                      order.payments?.[0]?.method === "credit" ? "Cr√©dito" : "Desconhecido",
        receivedAmount: order.payments?.[0]?.receivedAmount ? 
          `${parseFloat(order.payments[0].receivedAmount).toFixed(0)}` : undefined,
        change: order.payments?.[0]?.changeAmount ? 
          `${parseFloat(order.payments[0].changeAmount).toFixed(0)}` : undefined,
        cashier: order.server?.firstName || 'Servidor',
        timestamp: new Date(order.createdAt).toLocaleString('pt-PT', { 
          day: '2-digit', 
          month: '2-digit', 
          year: 'numeric', 
          hour: '2-digit', 
          minute: '2-digit' 
        })
      };

      await thermalPrinter.printReceipt(receiptData);
      toast({
        title: "Sucesso",
        description: "Recibo enviado para impress√£o",
      });
    } catch (error) {
      toast({
        title: "Erro de Impress√£o",
        description: error instanceof Error ? error.message : "Erro ao imprimir recibo",
        variant: "destructive",
      });
    }
  };

  const OrderReceiptDialog = ({ order }: { order: OrderWithItems }) => (
    <Dialog>
      <DialogTrigger asChild>
        <Button variant="outline" size="sm" className="border-gray-600 hover:bg-gray-700">
          <Eye className="w-4 h-4 mr-1" />
          Ver Recibo
        </Button>
      </DialogTrigger>
      <DialogContent className="bg-gray-800 border-gray-700 text-white max-w-md">
        <DialogHeader>
          <DialogTitle className="text-center">Recibo de Venda</DialogTitle>
        </DialogHeader>
        <div className="space-y-4">
          <div className="text-center border-b border-gray-600 pb-4">
            <h3 className="font-bold">BarManager Pro</h3>
            <p className="text-sm text-gray-400">Recibo #{order.id}</p>
            <p className="text-xs text-gray-500">
              {new Date(order.createdAt).toLocaleString("pt-PT")}
            </p>
          </div>

          <div className="space-y-2">
            <div className="flex justify-between text-sm">
              <span>Mesa:</span>
              <span>{order.table?.number}</span>
            </div>
            <div className="flex justify-between text-sm">
              <span>Servidor:</span>
              <span>{order.server?.firstName}</span>
            </div>
            <div className="flex justify-between text-sm">
              <span>Cliente:</span>
              <span>{order.clientName || "Cliente An√¥nimo"}</span>
            </div>
          </div>

          <div className="border-t border-gray-600 pt-4">
            <h4 className="font-medium mb-2">Itens:</h4>
            <div className="space-y-1">
              {order.items.map((item) => (
                <div key={item.id} className="flex justify-between text-sm">
                  <span>{item.quantity}x {item.product.name}</span>
                  <span>{formatCurrency(parseFloat(item.totalPrice))}</span>
                </div>
              ))}
            </div>
          </div>

          <div className="border-t border-gray-600 pt-4 space-y-2">
            <div className="flex justify-between font-medium">
              <span>Total:</span>
              <span className="text-green-400">{formatCurrency(order.totalAmount)}</span>
            </div>
            <div className="flex justify-between text-sm">
              <span>Pagamento:</span>
              {getPaymentMethodBadge(order.paymentMethod || 'cash')}
            </div>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-2xl font-bold text-white flex items-center">
            <History className="w-8 h-8 mr-3" />
            Hist√≥rico de Vendas
          </h2>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {[1, 2, 3, 4, 5, 6].map((i) => (
            <Card key={i} className="bg-gray-800 border-gray-700">
              <CardContent className="p-4">
                <div className="animate-pulse">
                  <div className="h-4 bg-gray-700 rounded w-3/4 mb-2"></div>
                  <div className="h-3 bg-gray-700 rounded w-1/2 mb-2"></div>
                  <div className="h-3 bg-gray-700 rounded w-2/3"></div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-2xl font-bold text-white flex items-center">
          <History className="w-8 h-8 mr-3" />
          Hist√≥rico de Vendas
        </h2>
        <Badge variant="secondary" className="bg-gray-700 text-white">
          {filteredOrders.length} vendas
        </Badge>
      </div>

      {/* Filters */}
      <Card className="bg-gray-800 border-gray-700 mb-6">
        <CardHeader>
          <CardTitle className="text-lg text-white">Filtros</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <Label htmlFor="search" className="text-sm font-medium text-gray-300">
                Pesquisar
              </Label>
              <div className="relative">
                <Search className="absolute left-3 top-3 w-4 h-4 text-gray-400" />
                <Input
                  id="search"
                  placeholder="Mesa, servidor, cliente ou produto..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="bg-gray-700 border-gray-600 text-white pl-10"
                />
              </div>
            </div>
            <div>
              <Label htmlFor="date" className="text-sm font-medium text-gray-300">
                Data
              </Label>
              <div className="relative">
                <Calendar className="absolute left-3 top-3 w-4 h-4 text-gray-400" />
                <Input
                  id="date"
                  type="date"
                  value={selectedDate}
                  onChange={(e) => setSelectedDate(e.target.value)}
                  className="bg-gray-700 border-gray-600 text-white pl-10"
                />
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Sales Summary */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">Total de Vendas</p>
                <p className="text-2xl font-bold text-green-400">
                  {formatCurrency(
                    filteredOrders.reduce((sum, order) => sum + parseFloat(order.totalAmount), 0)
                  )}
                </p>
              </div>
              <DollarSign className="w-8 h-8 text-green-400" />
            </div>
          </CardContent>
        </Card>

        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">N√∫mero de Vendas</p>
                <p className="text-2xl font-bold text-blue-400">{filteredOrders.length}</p>
              </div>
              <Receipt className="w-8 h-8 text-blue-400" />
            </div>
          </CardContent>
        </Card>

        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">Venda M√©dia</p>
                <p className="text-2xl font-bold text-purple-400">
                  {formatCurrency(
                    filteredOrders.length > 0 
                      ? filteredOrders.reduce((sum, order) => sum + parseFloat(order.totalAmount), 0) / filteredOrders.length
                      : 0
                  )}
                </p>
              </div>
              <User className="w-8 h-8 text-purple-400" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Orders List */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {filteredOrders.length === 0 ? (
          <div className="col-span-full text-center py-12">
            <Receipt className="w-16 h-16 mx-auto mb-4 text-gray-500" />
            <p className="text-gray-400">Nenhuma venda encontrada</p>
          </div>
        ) : (
          filteredOrders.map((order) => (
            <Card key={order.id} className="bg-gray-800 border-gray-700">
              <CardContent className="p-4">
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center space-x-2">
                    <Badge className="bg-green-500 text-white">
                      Mesa {order.table?.number}
                    </Badge>
                    {getPaymentMethodBadge(order.paymentMethod || 'cash')}
                  </div>
                  <span className="text-sm text-gray-400">
                    #{order.id}
                  </span>
                </div>

                <div className="space-y-2 mb-3">
                  <div className="flex items-center justify-between text-sm">
                    <div className="flex items-center space-x-1">
                      <Clock className="w-4 h-4 text-gray-400" />
                      <span className="text-gray-300">
                        {new Date(order.createdAt).toLocaleTimeString("pt-PT", {
                          hour: "2-digit",
                          minute: "2-digit",
                        })}
                      </span>
                    </div>
                    <span className="text-green-400 font-bold">
                      {formatCurrency(order.totalAmount)}
                    </span>
                  </div>

                  <div className="flex items-center justify-between text-sm">
                    <div className="flex items-center space-x-1">
                      <User className="w-4 h-4 text-gray-400" />
                      <span className="text-gray-300">
                        {order.server?.firstName}
                      </span>
                    </div>
                    <span className="text-gray-400">
                      {order.items.length} itens
                    </span>
                  </div>
                </div>

                <div className="space-y-1 mb-3">
                  {order.items.slice(0, 2).map((item) => (
                    <div key={item.id} className="flex justify-between text-xs text-gray-400">
                      <span>{item.quantity}x {item.product.name}</span>
                      <span>{formatCurrency(parseFloat(item.totalPrice))}</span>
                    </div>
                  ))}
                  {order.items.length > 2 && (
                    <div className="text-xs text-gray-500">
                      +{order.items.length - 2} mais itens
                    </div>
                  )}
                </div>

                <div className="flex space-x-2">
                  <OrderReceiptDialog order={order} />
                  <Button
                    onClick={() => handlePrintReceipt(order)}
                    variant="outline"
                    size="sm"
                    className="border-green-600 text-green-400 hover:bg-green-600/20"
                  >
                    <Printer className="w-4 h-4 mr-1" />
                    Imprimir
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

client/src/pages/Tables.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Table, OrderWithItems } from "@shared/schema";
import { Plus, Users, Clock, ShoppingCart, Receipt, Edit } from "lucide-react";
import { PT } from "@/lib/i18n";
import { useState } from "react";
import { formatCurrency } from "@/lib/currency";

export default function Tables() {
  const [selectedTable, setSelectedTable] = useState<Table | null>(null);

  const { data: tables, isLoading } = useQuery<Table[]>({
    queryKey: ["/api/tables"],
    refetchInterval: 5000,
  });

  const { data: orders } = useQuery<OrderWithItems[]>({
    queryKey: ["/api/orders/pending"],
    refetchInterval: 5000,
  });

  // Ensure arrays are always defined
  const tablesList = tables || [];
  const ordersList = orders || [];

  const getStatusColor = (status: string) => {
    switch (status) {
      case "occupied":
        return "bg-red-500";
      case "free":
        return "bg-green-500";
      case "reserved":
        return "bg-yellow-500";
      default:
        return "bg-gray-500";
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case "occupied":
        return PT.tables.occupied;
      case "free":
        return PT.tables.free;
      case "reserved":
        return PT.tables.reserved;
      default:
        return status;
    }
  };

  const occupiedTables = tablesList.filter(t => t.status === "occupied").length;
  const freeTables = tablesList.filter(t => t.status === "free").length;
  const reservedTables = tablesList.filter(t => t.status === "reserved").length;

  const getTableOrder = (tableId: number): OrderWithItems | undefined => {
    return ordersList.find(order => order.tableId === tableId && order.status === "pending");
  };

  const getLocationName = (location: string) => {
    switch (location) {
      case 'main_hall':
        return 'Sala Principal';
      case 'balcony':
        return 'Varanda';
      case 'terrace':
        return 'Esplanada';
      default:
        return location;
    }
  };

  const groupTablesByLocation = (tables: Table[]) => {
    const grouped = tables.reduce((acc, table) => {
      if (!acc[table.location]) {
        acc[table.location] = [];
      }
      acc[table.location].push(table);
      return acc;
    }, {} as Record<string, Table[]>);

    // Sort tables by number within each location
    Object.keys(grouped).forEach(location => {
      grouped[location].sort((a, b) => a.number - b.number);
    });

    return grouped;
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <h2 className="text-2xl font-bold text-white mb-6">{PT.tables.title}</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
          {[1, 2, 3, 4, 5, 6, 7, 8].map((i) => (
            <Card key={i} className="bg-gray-800 border-gray-700">
              <CardContent className="p-4">
                <div className="animate-pulse">
                  <div className="h-8 bg-gray-600 rounded-full w-16 mx-auto mb-4"></div>
                  <div className="h-4 bg-gray-600 rounded w-3/4 mx-auto mb-2"></div>
                  <div className="h-3 bg-gray-600 rounded w-1/2 mx-auto"></div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-2xl font-bold text-white">Gestion des Tables</h2>
        <div className="flex space-x-2">
          <Button 
            onClick={() => window.location.href = '/orders'} 
            className="bg-orange-600 hover:bg-orange-700 text-white"
          >
            <ShoppingCart className="w-4 h-4 mr-2" />
            Nouveau Pedido
          </Button>
        </div>
      </div>

      {/* Status Summary */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">Total</p>
                <p className="text-2xl font-bold text-white">{tablesList.length}</p>
              </div>
              <div className="w-12 h-12 bg-blue-500 bg-opacity-20 rounded-full flex items-center justify-center">
                <Users className="w-6 h-6 text-blue-400" />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">Occup√©es</p>
                <p className="text-2xl font-bold text-red-400">{occupiedTables}</p>
              </div>
              <div className="w-12 h-12 bg-red-500 bg-opacity-20 rounded-full flex items-center justify-center">
                <Clock className="w-6 h-6 text-red-400" />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">Libres</p>
                <p className="text-2xl font-bold text-green-400">{freeTables}</p>
              </div>
              <div className="w-12 h-12 bg-green-500 bg-opacity-20 rounded-full flex items-center justify-center">
                <Users className="w-6 h-6 text-green-400" />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-400">R√©serv√©es</p>
                <p className="text-2xl font-bold text-yellow-400">{reservedTables}</p>
              </div>
              <div className="w-12 h-12 bg-yellow-500 bg-opacity-20 rounded-full flex items-center justify-center">
                <Clock className="w-6 h-6 text-yellow-400" />
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Tables by Location */}
      {Object.entries(groupTablesByLocation(tablesList)).map(([location, locationTables]) => (
        <div key={location} className="mb-8">
          <h3 className="text-xl font-bold text-white mb-4 flex items-center">
            <Users className="w-5 h-5 mr-2 text-orange-400" />
            {getLocationName(location)}
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
            {locationTables.map((table) => {
              const tableOrder = getTableOrder(table.id);
              const hasPendingOrder = tableOrder !== undefined;

              return (
                <Card key={table.id} className="bg-gray-800 border-gray-700 hover:bg-gray-750 transition-colors">
                  <CardHeader className="pb-3">
                    <div className="flex items-center justify-between">
                      <CardTitle className="text-lg text-white">
                        Table {table.number}
                      </CardTitle>
                      <Badge className={`${getStatusColor(table.status)} text-white`}>
                        {getStatusText(table.status)}
                      </Badge>
                    </div>
                  </CardHeader>
                  <CardContent className="pt-0">
                    <div className="space-y-3">
                      <div className="flex items-center justify-center">
                        <div className={`w-16 h-16 rounded-full flex items-center justify-center text-2xl font-bold ${
                          table.status === 'free' 
                            ? 'bg-green-500 text-white' 
                            : hasPendingOrder
                            ? 'bg-orange-500 text-white'
                            : 'bg-red-500 text-white'
                        }`}>
                          {table.number}
                          {hasPendingOrder && <span className="text-xs ml-1">+</span>}
                        </div>
                      </div>

                      <div className="text-center">
                        <p className="text-sm text-gray-400">
                          Capacit√©: {table.capacity} personnes
                        </p>
                        <p className="text-sm text-gray-400">
                          {getLocationName(table.location)}
                        </p>
                      </div>

                      {/* Order Information */}
                      {tableOrder && (
                        <div className="bg-gray-700 rounded-lg p-3 space-y-2">
                          <div className="flex items-center justify-between">
                            <span className="text-sm text-gray-400">Client:</span>
                            <span className="text-sm text-white">
                              {tableOrder.clientName || tableOrder.creditClient?.name || 'An√¥nimo'}
                            </span>
                          </div>
                          <div className="flex items-center justify-between">
                            <span className="text-sm text-gray-400">Total:</span>
                            <span className="text-sm text-green-400 font-bold">
                              {formatCurrency(tableOrder.totalAmount)}
                            </span>
                          </div>
                          <div className="flex items-center justify-between">
                            <span className="text-sm text-gray-400">Itens:</span>
                            <span className="text-sm text-white">
                              {tableOrder.items.length} produtos
                            </span>
                          </div>
                        </div>
                      )}

                      {/* Action Buttons */}
                      <div className="flex space-x-2">
                        {table.status === 'free' ? (
                          <Button 
                            size="sm" 
                            className="flex-1 bg-orange-600 hover:bg-orange-700"
                            onClick={() => window.location.href = `/orders?table=${table.id}`}
                          >
                            <ShoppingCart className="w-4 h-4 mr-1" />
                            Novo Pedido
                          </Button>
                        ) : hasPendingOrder ? (
                          <>
                            <Dialog>
                              <DialogTrigger asChild>
                                <Button 
                                  size="sm" 
                                  variant="outline" 
                                  className="flex-1 border-blue-600 text-blue-400 hover:bg-blue-600/20"
                                >
                                  <Receipt className="w-4 h-4 mr-1" />
                                  Ver
                                </Button>
                              </DialogTrigger>
                              <DialogContent className="bg-gray-800 border-gray-700">
                                <DialogHeader>
                                  <DialogTitle className="text-white">
                                    Commande Table {table.number}
                                  </DialogTitle>
                                </DialogHeader>
                                {tableOrder && (
                                  <div className="space-y-4">
                                    <div className="bg-gray-700 rounded-lg p-4">
                                      <h4 className="font-medium text-white mb-2">D√©tails de la commande</h4>
                                      <div className="space-y-2 text-sm">
                                        <div className="flex justify-between">
                                          <span className="text-gray-400">Client:</span>
                                          <span className="text-white">{tableOrder.clientName || tableOrder.creditClient?.name || 'An√¥nimo'}</span>
                                        </div>
                                        <div className="flex justify-between">
                                          <span className="text-gray-400">Serveur:</span>
                                          <span className="text-white">{tableOrder.server?.firstName || 'N/A'}</span>
                                        </div>
                                        <div className="flex justify-between">
                                          <span className="text-gray-400">Total:</span>
                                          <span className="text-green-400 font-bold">{formatCurrency(tableOrder.totalAmount)}</span>
                                        </div>
                                      </div>
                                    </div>

                                    <div className="bg-gray-700 rounded-lg p-4">
                                      <h4 className="font-medium text-white mb-2">Articles ({tableOrder.items.length})</h4>
                                      <div className="space-y-2">
                                        {tableOrder.items.map((item, index) => (
                                          <div key={index} className="flex justify-between text-sm">
                                            <span className="text-gray-300">
                                              {item.quantity}x {item.product.name}
                                            </span>
                                            <span className="text-white">{formatCurrency(item.totalPrice)}</span>
                                          </div>
                                        ))}
                                      </div>
                                    </div>
                                  </div>
                                )}
                              </DialogContent>
                            </Dialog>
                            <Button 
                              size="sm" 
                              className="flex-1 bg-orange-600 hover:bg-orange-700"
                              onClick={() => window.location.href = `/orders?table=${table.id}`}
                            >
                              <Plus className="w-4 h-4 mr-1" />
                              Ajouter
                            </Button>
                          </>
                        ) : (
                          <Button 
                            size="sm" 
                            variant="outline" 
                            className="flex-1 border-gray-600 hover:bg-gray-700"
                            disabled
                          >
                            Table occup√©e
                          </Button>
                        )}
                      </div>
                    </div>
                  </CardContent>
                </Card>
              );
            })}
          </div>
        </div>
      ))}
    </div>
  );
}

