import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { UserPlus } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

const newClientSchema = z.object({
  name: z.string().min(2, "Nome deve ter pelo menos 2 caracteres"),
  email: z.string().email("Email inv√°lido").optional().or(z.literal("")),
  phone: z.string().min(1, "Telefone √© obrigat√≥rio"),
  address: z.string().optional(),
  notes: z.string().optional(),
});

type NewClientForm = z.infer<typeof newClientSchema>;

interface NewClientModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onClientCreated?: (client: any) => void;
}

export function NewClientModal({ open, onOpenChange, onClientCreated }: NewClientModalProps) {
  const queryClient = useQueryClient();
  const { toast } = useToast();

  console.log("NewClientModal render - open:", open);

  const form = useForm<NewClientForm>({
    resolver: zodResolver(newClientSchema),
    defaultValues: {
      name: "",
      email: "",
      phone: "",
      address: "",
      notes: "",
    },
  });

  const createClientMutation = useMutation({
    mutationFn: async (data: NewClientForm) => {
      console.log("Sending client data:", data);
      return apiRequest("POST", "/api/credit-clients", {
        name: data.name,
        phone: data.phone,
        email: data.email || null,
        address: data.address || null,
        notes: data.notes || null,
      });
    },
    onSuccess: (data) => {
      toast({
        title: "Sucesso",
        description: "Cliente criado com sucesso",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/credit-clients"] });
      onClientCreated?.(data);
      onOpenChange(false);
      form.reset();
    },
    onError: (error) => {
      toast({
        title: "Erro",
        description: "Erro ao criar cliente",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: NewClientForm) => {
    createClientMutation.mutate(data);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="bg-gray-800 border-gray-700 text-white max-w-md">
        <DialogHeader>
          <DialogTitle className="text-lg font-semibold flex items-center">
            <UserPlus className="w-5 h-5 mr-2 text-blue-400" />
            Novo Cliente de Cr√©dito
          </DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-sm font-medium text-gray-300">
                    Nome Completo *
                  </FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      placeholder="Ex: Jo√£o Silva"
                      className="bg-gray-700 border-gray-600 text-white"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="email"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-sm font-medium text-gray-300">
                    Email
                  </FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      type="email"
                      placeholder="Ex: joao@exemplo.com"
                      className="bg-gray-700 border-gray-600 text-white"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="phone"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-sm font-medium text-gray-300">
                    Telefone
                  </FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      type="tel"
                      placeholder="Ex: +245 123 456 789"
                      className="bg-gray-700 border-gray-600 text-white"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />



            <FormField
              control={form.control}
              name="address"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-sm font-medium text-gray-300">
                    Endere√ßo
                  </FormLabel>
                  <FormControl>
                    <Textarea
                      {...field}
                      placeholder="Endere√ßo completo..."
                      className="bg-gray-700 border-gray-600 text-white min-h-[60px]"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="notes"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-sm font-medium text-gray-300">
                    Observa√ß√µes
                  </FormLabel>
                  <FormControl>
                    <Textarea
                      {...field}
                      placeholder="Observa√ß√µes sobre o cliente..."
                      className="bg-gray-700 border-gray-600 text-white min-h-[60px]"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex space-x-3">
              <Button
                type="button"
                onClick={() => onOpenChange(false)}
                variant="outline"
                className="flex-1 border-gray-600 hover:bg-gray-700"
              >
                Cancelar
              </Button>
              <Button
                type="submit"
                disabled={createClientMutation.isPending}
                className="flex-1 bg-blue-600 hover:bg-blue-700"
              >
                {createClientMutation.isPending ? "Criando..." : "Criar Cliente"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/modals/OpenSession.content:
import { useState, useEffect } from "react";
import { useBarStore } from "@/store/useBarStore";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { Card, CardContent } from "@/components/ui/card";
import { PlayCircle, Clock } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

export function OpenSessionModal() {
  const { showOpenSessionModal, setShowOpenSessionModal, setActiveSession } = useBarStore();
  const [shiftType, setShiftType] = useState<"morning" | "evening">("morning");
  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Auto-detect shift type based on current time
  const getCurrentShiftType = () => {
    const hour = new Date().getHours();
    return hour < 14 ? "morning" : "evening";
  };

  // Set default shift type based on current time
  useEffect(() => {
    setShiftType(getCurrentShiftType());
  }, []);

  // Listen for auto-open events
  useEffect(() => {
    const handleOpenSessionModal = () => {
      setShowOpenSessionModal(true);
    };

    window.addEventListener("openSessionModal", handleOpenSessionModal);
    return () => window.removeEventListener("openSessionModal", handleOpenSessionModal);
  }, [setShowOpenSessionModal]);

  const openSessionMutation = useMutation({
    mutationFn: async () => {
      return apiRequest("POST", "/api/sessions", { shiftType });
    },
    onSuccess: (data) => {
      toast({
        title: "Sess√£o Aberta",
        description: `Sess√£o ${shiftType === "morning" ? "da manh√£" : "da tarde"} iniciada com sucesso`,
      });
      setActiveSession(data);
      queryClient.invalidateQueries({ queryKey: ["/api/sessions"] });
      setShowOpenSessionModal(false);
    },
    onError: (error) => {
      toast({
        title: "Erro",
        description: "Erro ao abrir sess√£o",
        variant: "destructive",
      });
    },
  });

  const handleOpenSession = () => {
    openSessionMutation.mutate();
  };

  return (
    <Dialog open={showOpenSessionModal} onOpenChange={setShowOpenSessionModal}>
      <DialogContent className="bg-gray-800 border-gray-700 text-white max-w-md">
        <DialogHeader>
          <DialogTitle className="text-lg font-semibold flex items-center">
            <PlayCircle className="w-5 h-5 mr-2 text-green-400" />
            Abrir Nova Sess√£o
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          <p className="text-gray-300">
            Selecione o tipo de turno para iniciar uma nova sess√£o de caixa. A sess√£o permanecer√° aberta at√© ser fechada manualmente.
          </p>

          <div className="space-y-2">
            <Label htmlFor="shift-type" className="text-sm font-medium">
              Tipo de Turno
            </Label>
            <Select value={shiftType} onValueChange={(value: "morning" | "evening") => setShiftType(value)}>
              <SelectTrigger className="bg-gray-700 border-gray-600 text-white">
                <SelectValue placeholder="Selecionar turno" />
              </SelectTrigger>
              <SelectContent className="bg-gray-700 border-gray-600">
                <SelectItem value="morning" className="text-white">
                  <div className="flex items-center">
                    <Clock className="w-4 h-4 mr-2" />
                    Turno da Manh√£
                  </div>
                </SelectItem>
                <SelectItem value="evening" className="text-white">
                  <div className="flex items-center">
                    <Clock className="w-4 h-4 mr-2" />
                    Turno da Tarde
                  </div>
                </SelectItem>
              </SelectContent>
            </Select>
          </div>

          <Card className="bg-gray-700 border-gray-600">
            <CardContent className="p-4">
              <h3 className="font-medium text-sm mb-2">Informa√ß√µes da Sess√£o</h3>
              <div className="space-y-1 text-sm">
                <div className="flex justify-between">
                  <span className="text-gray-400">Turno:</span>
                  <span className="text-white">
                    {shiftType === "morning" ? "Manh√£" : "Tarde"}
                  </span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-400">In√≠cio:</span>
                  <span className="text-white">
                    {new Date().toLocaleTimeString("pt-PT", {
                      hour: "2-digit",
                      minute: "2-digit",
                    })}
                  </span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-400">Data:</span>
                  <span className="text-white">
                    {new Date().toLocaleDateString("pt-PT")}
                  </span>
                </div>
              </div>
            </CardContent>
          </Card>

          <div className="flex space-x-3">
            <Button
              onClick={() => setShowOpenSessionModal(false)}
              variant="outline"
              className="flex-1 border-gray-600 hover:bg-gray-700"
            >
              Cancelar
            </Button>
            <Button
              onClick={handleOpenSession}
              disabled={openSessionMutation.isPending}
              className="flex-1 bg-green-600 hover:bg-green-700"
            >
              {openSessionMutation.isPending ? "Abrindo..." : "Abrir Sess√£o"}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/modals/PaymentModa.content:
import { useState } from "react";
import { useBarStore } from "@/store/useBarStore";
import { useMutation, useQueryClient, useQuery } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Card, CardContent } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { CreditCard, Banknote, FileText, X, Smartphone, Users, Crown } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { formatCurrency } from "@/lib/currency";
import { thermalPrinter } from "@/lib/thermalPrinter";
import { useAuth } from "@/hooks/useAuth";
import type { CreditClient } from "@shared/schema";

export function PaymentModal() {
  const { selectedOrder, showPaymentModal, setShowPaymentModal, setSelectedOrder } = useBarStore();
  const [paymentMethod, setPaymentMethod] = useState<"cash" | "mobile_money" | "credit" | "manager_consumption">("cash");
  const [receivedAmount, setReceivedAmount] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [selectedCreditClient, setSelectedCreditClient] = useState<number | null>(null);
  const queryClient = useQueryClient();
  const { toast } = useToast();
  const { user } = useAuth();

  // Managers allowed for free consumption (any cashier can process for them)
  const allowedManagersForFreeConsumption = ["carlmalack", "lucelle"];

  // Check if order is for one of the managers allowed for free consumption
  const isOrderForManagerConsumption = selectedOrder && (
    allowedManagersForFreeConsumption.includes(selectedOrder.clientName?.toLowerCase() || "") ||
    allowedManagersForFreeConsumption.some(manager => 
      selectedOrder.clientName?.toLowerCase().includes(manager) ||
      selectedOrder.creditClient?.name?.toLowerCase().includes(manager)
    )
  );

  // Fetch credit clients
  const { data: creditClients = [] } = useQuery<CreditClient[]>({
    queryKey: ["/api/credit-clients"],
  });

  const paymentMutation = useMutation({
    mutationFn: async (paymentData: any) => {
      return apiRequest("POST", "/api/payments", paymentData);
    },
    onSuccess: async (data) => {
      toast({
        title: "Sucesso",
        description: "Pagamento processado com sucesso",
      });

      // Print receipt automatically after payment
      if (selectedOrder) {
        try {
          // Get table information
          const table = await queryClient.getQueryData(["/api/tables"]) as any[];
          const tableInfo = table?.find(t => t.id === selectedOrder.tableId);

          // Get cashier info (assume current user from auth)
          const userData = await queryClient.getQueryData(["/api/auth/user"]) as any;

          // Convert order data to receipt format
          const receiptData = {
            orderNumber: selectedOrder.id.toString(),
            tableName: `Mesa ${tableInfo?.number || selectedOrder.tableId}`,
            clientName: selectedOrder.clientName || selectedOrder.creditClient?.name,
            items: selectedOrder.items.map((item: any) => ({
              name: item.product?.name || 'Produto',
              quantity: item.quantity,
              price: `${parseFloat(item.unitPrice || 0).toFixed(0)}`,
              total: `${parseFloat(item.totalPrice || 0).toFixed(0)}`
            })),
            subtotal: `${parseFloat(selectedOrder.totalAmount).toFixed(0)}`,
            total: `${parseFloat(selectedOrder.totalAmount).toFixed(0)}`,
            paymentMethod: paymentMethod === "cash" ? "Dinheiro" : 
                          paymentMethod === "mobile_money" ? "Mobile Money" : 
                          paymentMethod === "credit" ? "Cr√©dito" : "Consumo Ger√™ncia",
            receivedAmount: receivedAmount ? `${parseFloat(receivedAmount).toFixed(0)}` : undefined,
            change: receivedAmount ? `${(parseFloat(receivedAmount) - parseFloat(selectedOrder.totalAmount)).toFixed(0)}` : undefined,
            cashier: userData?.firstName || 'Caixa',
            timestamp: new Date().toLocaleString('pt-PT', { 
              day: '2-digit', 
              month: '2-digit', 
              year: 'numeric', 
              hour: '2-digit', 
              minute: '2-digit' 
            })
          };

          await thermalPrinter.printReceipt(receiptData);
          toast({
            title: "Recibo Impresso",
            description: "Recibo enviado para impress√£o",
          });
        } catch (error) {
          console.error('Erro ao imprimir recibo:', error);
          // Don't show error toast for print failure after successful payment
        }
      }

      queryClient.invalidateQueries({ queryKey: ["/api/orders"] });
      queryClient.invalidateQueries({ queryKey: ["/api/sessions"] });
      queryClient.invalidateQueries({ queryKey: ["/api/tables"] });
      queryClient.invalidateQueries({ queryKey: ["/api/credit-clients"] });
      handleClose();
    },
    onError: (error) => {
      toast({
        title: "Erro",
        description: "Erro ao processar pagamento",
        variant: "destructive",
      });
    },
  });

  const handleClose = () => {
    setShowPaymentModal(false);
    setSelectedOrder(null);
    setPaymentMethod("cash");
    setReceivedAmount("");
    setPhoneNumber("");
    setSelectedCreditClient(null);
  };

  const handlePayment = () => {
    if (!selectedOrder) return;

    // Validation based on payment method
    if (paymentMethod === "cash" && (!receivedAmount || parseFloat(receivedAmount) < parseFloat(selectedOrder.totalAmount))) {
      toast({
        title: "Erro",
        description: "Valor recebido deve ser maior ou igual ao total",
        variant: "destructive",
      });
      return;
    }

    if (paymentMethod === "manager_consumption" && !isOrderForManagerConsumption) {
      toast({
        title: "Erro",
        description: "Consumo gratuito dispon√≠vel apenas para os gerentes Carl Malack e Lucelle Reis",
        variant: "destructive",
      });
      return;
    }

    if (paymentMethod === "mobile_money" && !phoneNumber) {
      toast({
        title: "Erro",
        description: "Por favor, insira o n√∫mero de telefone",
        variant: "destructive",
      });
      return;
    }

    if (paymentMethod === "credit" && !selectedCreditClient) {
      toast({
        title: "Erro",
        description: "Por favor, selecione um cliente de cr√©dito",
        variant: "destructive",
      });
      return;
    }

    const paymentData = {
      orderId: selectedOrder.id,
      method: paymentMethod,
      amount: paymentMethod === "manager_consumption" ? "0.00" : selectedOrder.totalAmount,
      receivedAmount: paymentMethod === "cash" ? parseFloat(receivedAmount) : 
                    paymentMethod === "manager_consumption" ? "0.00" : null,
      creditClientId: paymentMethod === "credit" ? selectedCreditClient : null,
      phoneNumber: paymentMethod === "mobile_money" ? phoneNumber : null,
    };

    paymentMutation.mutate(paymentData);
  };

  const calculateChange = () => {
    if (!selectedOrder || !receivedAmount) return 0;
    return Math.max(0, parseFloat(receivedAmount) - parseFloat(selectedOrder.totalAmount));
  };

  if (!selectedOrder) return null;

  return (
    <Dialog open={showPaymentModal} onOpenChange={setShowPaymentModal}>
      <DialogContent className="bg-gray-800 border-gray-700 text-white max-w-md">
        <DialogHeader>
          <DialogTitle className="text-lg font-semibold">Processamento de Pagamento</DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {/* Order Summary */}
          <Card className="bg-gray-700 border-gray-600">
            <CardContent className="p-4">
              <div className="flex justify-between items-center mb-2">
                <span className="font-medium">Mesa {selectedOrder.table?.number || "N/A"}</span>
                <span className="text-green-400 font-bold">{formatCurrency(selectedOrder.totalAmount)}</span>
              </div>
              <div className="text-sm text-gray-400">
                {selectedOrder.items.map((item) => (
                  <div key={item.id} className="flex justify-between">
                    <span>{item.quantity}x {item.product.name}</span>
                    <span>{formatCurrency(parseFloat(item.unitPrice) * item.quantity)}</span>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>

          {/* Payment Method Selection */}
          <div className={`grid gap-3 ${isOrderForManagerConsumption ? 'grid-cols-2' : 'grid-cols-3'}`}>
            <Button
              onClick={() => setPaymentMethod("cash")}
              variant={paymentMethod === "cash" ? "default" : "outline"}
              className={`py-6 flex-col ${
                paymentMethod === "cash"
                  ? "bg-green-600 hover:bg-green-700"
                  : "border-gray-600 hover:bg-gray-700"
              }`}
            >
              <Banknote className="w-6 h-6 mb-1" />
              <span className="text-sm">Dinheiro</span>
            </Button>
            <Button
              onClick={() => setPaymentMethod("mobile_money")}
              variant={paymentMethod === "mobile_money" ? "default" : "outline"}
              className={`py-6 flex-col ${
                paymentMethod === "mobile_money"
                  ? "bg-blue-600 hover:bg-blue-700"
                  : "border-gray-600 hover:bg-gray-700"
              }`}
            >
              <Smartphone className="w-6 h-6 mb-1" />
              <span className="text-sm">Mobile Money</span>
            </Button>
            {!isOrderForManagerConsumption && (
              <Button
                onClick={() => setPaymentMethod("credit")}
                variant={paymentMethod === "credit" ? "default" : "outline"}
                className={`py-6 flex-col ${
                  paymentMethod === "credit"
                    ? "bg-orange-600 hover:bg-orange-700"
                    : "border-gray-600 hover:bg-gray-700"
                }`}
              >
                <Users className="w-6 h-6 mb-1" />
                <span className="text-sm">Cr√©dito</span>
              </Button>
            )}
            {isOrderForManagerConsumption && (
              <Button
                onClick={() => setPaymentMethod("manager_consumption")}
                variant={paymentMethod === "manager_consumption" ? "default" : "outline"}
                className={`py-6 flex-col ${
                  paymentMethod === "manager_consumption"
                    ? "bg-purple-600 hover:bg-purple-700"
                    : "border-gray-600 hover:bg-gray-700"
                } col-span-1`}
              >
                <Crown className="w-6 h-6 mb-1" />
                <span className="text-sm">Consumo Ger√™ncia</span>
              </Button>
            )}
          </div>

          {/* Show Credit option for manager orders if they want to pay for someone else */}
          {isOrderForManagerConsumption && (
            <div className="grid grid-cols-1 gap-3">
              <Button
                onClick={() => setPaymentMethod("credit")}
                variant={paymentMethod === "credit" ? "default" : "outline"}
                className={`py-4 flex-col ${
                  paymentMethod === "credit"
                    ? "bg-orange-600 hover:bg-orange-700"
                    : "border-gray-600 hover:bg-gray-700"
                }`}
              >
                <Users className="w-5 h-5 mb-1" />
                <span className="text-sm">Pagar para Cliente (Cr√©dito)</span>
              </Button>
            </div>
          )}

          {/* Payment Method Forms */}
          {paymentMethod === "cash" && (
            <div className="space-y-2">
              <Label htmlFor="received" className="text-sm font-medium">
                Valor Recebido
              </Label>
              <Input
                id="received"
                type="number"
                step="0.01"
                value={receivedAmount}
                onChange={(e) => setReceivedAmount(e.target.value)}
                placeholder={selectedOrder.totalAmount}
                className="bg-gray-700 border-gray-600 text-white"
              />
              {receivedAmount && (
                <p className="text-sm text-gray-400">
                  Troco: {formatCurrency(calculateChange())}
                </p>
              )}
            </div>
          )}

          {paymentMethod === "mobile_money" && (
            <div className="space-y-2">
              <Label htmlFor="phone" className="text-sm font-medium">
                N√∫mero de Telefone
              </Label>
              <Input
                id="phone"
                type="tel"
                value={phoneNumber}
                onChange={(e) => setPhoneNumber(e.target.value)}
                placeholder="Ex: +245 123 456 789"
                className="bg-gray-700 border-gray-600 text-white"
              />
              <p className="text-sm text-gray-400">
                Total: {formatCurrency(selectedOrder.totalAmount)}
              </p>
            </div>
          )}

          {paymentMethod === "credit" && (
            <div className="space-y-2">
              <Label htmlFor="client" className="text-sm font-medium">
                Cliente de Cr√©dito
              </Label>
              <Select value={selectedCreditClient?.toString()} onValueChange={(value) => setSelectedCreditClient(parseInt(value))}>
                <SelectTrigger className="bg-gray-700 border-gray-600 text-white">
                  <SelectValue placeholder="Selecionar cliente" />
                </SelectTrigger>
                <SelectContent className="bg-gray-700 border-gray-600">
                  {creditClients.map((client) => (
                    <SelectItem key={client.id} value={client.id.toString()} className="text-white">
                      {client.name} - {formatCurrency(client.totalCredit)} cr√©dito
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              {selectedCreditClient && (
                <p className="text-sm text-gray-400">
                  Ser√° adicionado ao cr√©dito: {formatCurrency(selectedOrder.totalAmount)}
                </p>
              )}
            </div>
          )}

          {paymentMethod === "manager_consumption" && (
            <div className="space-y-2">
              <div className="bg-purple-500 bg-opacity-20 p-4 rounded-lg border border-purple-500">
                <div className="flex items-center space-x-2 mb-2">
                  <Crown className="w-5 h-5 text-purple-400" />
                  <h3 className="font-medium text-purple-400">Consumo de Ger√™ncia</h3>
                </div>
                <p className="text-sm text-gray-300">
                  Este consumo ser√° registrado como gratuito para o gerente {selectedOrder.clientName || selectedOrder.creditClient?.name}.
                </p>
                <p className="text-xs text-gray-400 mt-2">
                  Total: {formatCurrency(selectedOrder.totalAmount)} - <span className="text-green-400 font-medium">GRATUITO</span>
                </p>
              </div>
            </div>
          )}

          {/* Action Buttons */}
          <div className="flex space-x-3">
            <Button
              onClick={handleClose}
              variant="outline"
              className="flex-1 border-gray-600 hover:bg-gray-700"
            >
              Cancelar
            </Button>
            <Button
              onClick={handlePayment}
              disabled={paymentMutation.isPending}
              className="flex-1 bg-blue-600 hover:bg-blue-700"
            >
              {paymentMutation.isPending ? "Processando..." : "Confirmar Pagamento"}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/modals/SessionModa.content:
import { useBarStore } from "@/store/useBarStore";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Card, CardContent } from "@/components/ui/card";
import { AlertTriangle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { formatCurrency } from "@/lib/currency";

export function SessionModal() {
  const { activeSession, sessionStats, showSessionModal, setShowSessionModal, setActiveSession } = useBarStore();
  const queryClient = useQueryClient();
  const { toast } = useToast();

  const endSessionMutation = useMutation({
    mutationFn: async () => {
      if (!activeSession) throw new Error("No active session");
      return apiRequest("POST", `/api/sessions/${activeSession.id}/end`, {});
    },
    onSuccess: () => {
      toast({
        title: "Sess√£o Encerrada",
        description: "A sess√£o foi encerrada com sucesso",
      });
      setActiveSession(null);
      queryClient.invalidateQueries({ queryKey: ["/api/sessions"] });
      setShowSessionModal(false);
    },
    onError: (error) => {
      toast({
        title: "Erreur",
        description: "Erreur lors de la fermeture de la session",
        variant: "destructive",
      });
    },
  });

  const handleEndSession = () => {
    endSessionMutation.mutate();
  };

  if (!activeSession) return null;

  return (
    <Dialog open={showSessionModal} onOpenChange={setShowSessionModal}>
      <DialogContent className="bg-gray-800 border-gray-700 text-white max-w-md">
        <DialogHeader>
          <DialogTitle className="text-lg font-semibold flex items-center">
            <AlertTriangle className="w-5 h-5 mr-2 text-orange-400" />
            Fermer la Session
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          <p className="text-gray-300">
            √ätes-vous s√ªr de vouloir fermer la session actuelle ? Cette action ne peut pas √™tre annul√©e.
          </p>

          <Card className="bg-gray-700 border-gray-600">
            <CardContent className="p-4">
              <h3 className="font-medium text-sm mb-3">R√©sum√© de la Session</h3>
              <div className="space-y-2">
                <div className="flex justify-between text-sm">
                  <span className="text-gray-400">Type:</span>
                  <span className="text-white">
                    {activeSession.shiftType === "morning" ? "Matin" : "Soir"}
                  </span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-gray-400">D√©but:</span>
                  <span className="text-white">
                    {new Date(activeSession.startTime).toLocaleTimeString("fr-FR", {
                      hour: "2-digit",
                      minute: "2-digit",
                    })}
                  </span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-gray-400">Ventes totales:</span>
                  <span className="text-green-400">{formatCurrency(sessionStats?.totalSales || "0.00")}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-gray-400">Nombre de transactions:</span>
                  <span className="text-white">{sessionStats?.transactionCount || 0}</span>
                </div>
              </div>
            </CardContent>
          </Card>

          <div className="flex space-x-3">
            <Button
              onClick={() => setShowSessionModal(false)}
              variant="outline"
              className="flex-1 border-gray-600 hover:bg-gray-700"
            >
              Annuler
            </Button>
            <Button
              onClick={handleEndSession}
              disabled={endSessionMutation.isPending}
              className="flex-1 bg-red-600 hover:bg-red-700"
            >
              {endSessionMutation.isPending ? "Fermeture..." : "Fermer Session"}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

